query "FinalPrice"
total : TotalPrice ( salePrice > 0.0 );
order : Order (orderId == total.orderId);
end

ruleflow-group 'price-calc'
salience 20
dialect 'java'

when
  order : Order();

  item : OrderProduct ( itemCnt : cnt, stdPrice == 0 )
         from order.getLines();

  party : Party ( partyId == order.partyId, 
          partyTypeId == Party.REGULAR  ); 

  ProductPrice ( price : price, productId ==
          item.productId, priceType == 
          ProductPrice.LIST_PRICE);

then
  item.setStdPrice( price * itemCnt );

  update ( order );

  price_calc_results.add ( "Regular-LineItemPrice" );

ruleflow-group 'price-calc'
salience 20
dialect 'java'

when
  order : Order();

  item : OrderProduct ( itemCnt : cnt, stdPrice == 0 ) 
         from order.getLines();

  party : Party ( partyId == order.partyId, partyTypeId in 
          ( Party.SILVER, Party.GOLD ) ); 

  ProductPrice ( price : price, 
          productId == item.productId, priceType == 
          ProductPrice.DISCOUNT_PRICE);

then

  item.setStdPrice ( price * itemCnt );

  update ( order );

  price_calc_results.add ( "Discount-LineItemPrice" );

ruleflow-group 'price-calc'
salience 30
dialect 'java'

when
  order : Order();
 
  item : OrderProduct ( qntDiscountPrice == 0,   

  price : stdPrice, cnt >= 10, 
         stdPrice > 0 ) from order.getLines();

then

  item.setQntDiscountPrice ( price * .90f );

  update ( order );

  price_calc_results.add ( "Quantity-Discount" );

ruleflow-group 'price-calc'
salience 5
no-loop true
dialect 'java'

when
  order : Order();

  totalPrice : Float() from accumulate ( 
    OrderProduct ( disprice : qntDiscountPrice, stdprice :  stdPrice > 0 ) 
    from order.getLines(), init ( float total = 0; ),
    action ( total += (disprice > 0 ? disprice : stdprice); ),
    result ( new Float(total) ) 
    );

then

  TotalPrice tot = new TotalPrice();

  tot.setTotalPrice ( totalPrice );

  tot.setOrderId ( order.getOrderId() );

  insert ( tot );

  price_calc_results.add ( "TotalEndPrice" );

ruleflow-group 'shipment-calc'
salience 130
dialect 'java'

when

 price : TotalPrice ( shippingPrice > 0.0 );

 order : Order ( orderId == price.orderId, shippingCarrier == Shipping.USPS );

 eval ( price.shippingPrice < 12 );

then

 price.setShippingPrice(12.0f);

 update ( price );

 shipment_calc_results.add ( "ApplyUSPSMinimum" );

ruleflow-group 'shipment-calc'
salience 120
dialect 'java'

when

  price : TotalPrice ( shippingPrice > 0.0 );

  order : Order ( orderId == price.orderId );

  eval ( price.shippingPrice < 10 );

then

  price.setShippingPrice(10.0f);

  update ( price );

  shipment_calc_results.add ( "ApplyShipmentMinimum" );

ruleflow-group 'shipment-calc'
salience 150
dialect 'java'

when
  price : TotalPrice ( shippingPrice == 0.0 );

  order : Order ( orderId == price.orderId ); 

  shipPrice : Float ( ) from accumulate ( 

  orderProd : OrderProduct ( stdShipping :  stdShipping ) from order.getLines(),
	      init ( float total = 0; ),
	      action ( total += stdShipping; ),
	      result ( new Float(total) ) 
	      );

then
  price.setShippingPrice( shipPrice );

  update ( price );

  shipment_calc_results.add ( "CalculateShippingTotal" );

ruleflow-group 'shipment-calc'
salience 200
dialect 'java'

when 
  price : TotalPrice ( );

  order : Order ( orderId == price.orderId, orderItems : lines ); 
  
  orderItem : OrderProduct ( stdprice :  stdPrice, stdShipping == 0.0 ) from    
              order.getLines();

  prodShipping : Product ( productId == orderItem.productId );

  shipping : Shipping (carrier == order.shippingCarrier, shippingMethod == 
             order.shippingMethod );

then
  orderItem.setStdShipping(prodShipping.getWeight() * shipping.getMultiplier() * 
    orderItem.getCnt());

  update ( order );

  shipment_calc_results.add ( "LineShipmentCost" );

ruleflow-group 'special-calc'
activation-group 'final-price'
salience 50
dialect 'java'

when

  price : TotalPrice ( salePrice == 0.0);

  order : Order (orderId == price.orderId);

then

  System.out.println( "Currency is: " + order.getCurrency());

  price.setSalePrice ( FunctionHelper.currencyConverter(order.getCurrency(), 
      price.getShippingPrice() + price.getTotalPrice() ));

  update ( price );

  System.out.println ( "** FINAL TOTALS **" );

  System.out.println ( "   Order Id:   " + price.getOrderId());
 
  System.out.println ( "   Price:      " + price.getTotalPrice());

  System.out.println ( "   Shipping:   " + price.getShippingPrice());

  System.out.println ( "   Sale Price: " + price.getSalePrice() + " ( " +
     order.getCurrency() + " )");

  update ( price );

  special_calc_results.add ( "FinalTotals" );

ruleflow-group 'special-calc'
activation-group 'final-price'
salience 55
dialect 'java'

when
  price : TotalPrice ( salePrice == 0.0);
 
  order : Order (orderId == price.orderId);

  party : Party ( partyId == order.partyId, partyTypeId == Party.GOLD  );

then
  price.setSalePrice ( FunctionHelper.currencyConverter(order.getCurrency(),
    price.getTotalPrice() ));

  update ( price );

  System.out.println ( "** FINAL TOTALS FOR GOLD **" );
  System.out.println ( "   Order Id:   " + price.getOrderId());
  System.out.println ( "   Price:      " + price.getTotalPrice());
  System.out.println ( "   Shipping:   " + 0);
  System.out.println ( "   Sale Price: " + price.getSalePrice() + " ( " + 
    order.getCurrency() + " )");

  update ( price );

  special_calc_results.add ( "FinalTotalsGold" );

